import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";

export default function Navigation({ device, onSpecialsClick, onAboutClick }) {
  const [isScrolled, setIsScrolled] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      // Adjust the threshold (e.g., 50) as needed
      if (window.scrollY > 50) {
        setIsScrolled(true);
      } else {
        setIsScrolled(false);
      }
    };

    // Attach the scroll event listener
    window.addEventListener("scroll", handleScroll);

    // Cleanup on unmount
    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

  return (
    <section className={`navbar-stack ${isScrolled ? "scrolled" : ""}`}>
      {device === "mobile" ? (
        ""
      ) : (
        <Link className="nav-bar-logo" to="/">
          <img
            src={require("../assets/nav-logo.png")}
            alt="Little Lemon logo"
            className="nav-image"
          />
          <section className="logo-title">
            <h1>OASIS CLUB</h1>
          </section>
        </Link>
      )}
      <menu className={`navbar-menu ${device}`}>
        <Link className="hover-effect" to="/">
          <h1>Home</h1>
        </Link>
        <a className="hover-effect" onClick={onAboutClick}>
          <h1>About</h1>
        </a>
        <a
          className="hover-effect"
          href={require("../assets/menu.webp")}
          target="_blank"
          rel="noreferrer"
        >
          <h1>Menu</h1>
        </a>
        <Link className="hover-effect" to="/reservations">
          <h1>Reservations</h1>
        </Link>
        <section className="socials">
          <a
            className="hover-effect"
            href="https://www.instagram.com/dineroasis/"
            target="_blank"
            rel="noopener noreferrer"
          >
            {/* Your SVG icon */}
          </a>
          <a
            className="hover-effect"
            href="https://www.instagram.com/dineroasis/"
            target="_blank"
            rel="noopener noreferrer"
          >
            {/* Your SVG icon */}
          </a>
        </section> 
      </menu>
    </section>
  );
}
